CREATE TABLE IF NOT EXISTS song(id INTEGER PRIMARY KEY AUTOINCREMENT,title TEXT, verse1 TEXT, verse2 TEXT, verse3 TEXT, verse4 TEXT, verse5 TEXT, verse6 TEXT);
INSERT or IGNORE INTO song VALUES (1, 'Hyel TlaKǝu Janǝ Ea Mangha', 'Ada Hyellau Tlakǝn ea, janǝ ea hudengha. Nya wur-vi na tǝ ea hya''ati ga shil a mangha. Ea kwa falǝ Tlǝkemǝngha, nakǝ ngǝ nder dlǝuya, Ea shil hǝseli ngha, nake nde karti', 'Adan ea na Tlaken ea, nakǝ ngǝ tangermar seu, Nakǝ te nǝ ea zer ngha Yesu ka cha dleuya ea. Na ea nji babalhǝr ka ngate tehude ne ea. Ea shil hǝsǝli ngha, nakǝ karti', 'Yesu nakǝ Tlaken ea, nakǝ ngǝ ndǝr dleuya, Nak te pǝlya njir nǝu sǝrtakǝ ku mavakurnyi. Nak  te pǝnda mashingha aka dehekura nji. Ea shil hǝsǝli ngha, nakǝ nde karti', 'Shanguɗe Tlaken ea, tlǝmǝnnghe na karti, Nake nǝ atǝ dlǝdlǝbtiya ea lakur uya pi. Nakǝ ngǝ gau kǝeam wa atanyi ea kwa neu ngha. Ea shil hǝsǝli ngha, nakǝ ndǝ karti.', 'Ada Hyel Tlakǝn ea, makǝr aku chir zhang, Kyara ndǝ da kwa mbar dǝkar sekera tlǝmǝnghǝ wa. Ada, zǝr, na Shanguɗǝ, makǝr aku chir zhang, Ea shil hǝsǝli ngha, nakǝ ndǝ karti.', '');
INSERT or IGNORE INTO song VALUES (2, 'TlaKǝn Ea Ana Nder Dlǝuya', 'Tlakǝn ea, ana nder dleuya, Ka men daɓtǝ ea shili. Atǝ dunǝya ɓelsar dawa, Kǝl nda a tsakǝ ngǝ ɓǝlsar.', 'Parakǝ sakana ka ba''anawa, Ɓǝlǝr tlǝmngha a dawa! Nakǝ ngǝ tǝ ɓaltɓti dunǝya, Nakǝ kwa ɓaltǝ mbwar ngya kinda.','Kǝrsti Yesu, ndǝr dlǝuya ea, Vanya vala da tsa ea wa, Kǝl nyidangha nda pa kwa wura, Nacha ngǝ lǝgǝ njir nǝungha.', 'Nyida ngha ba ǝnya tsǝu tanyi, Parak tanyi ka sa ɓwaɓwatǝu. A ku dzǝga tǝlkur Hyellǝu ka, Ka ngya ka ga ea uya pi', 'Dǝgalkur, fala usaku, Ea kwa nǝngha Yesu zǝr Hyel! Nyandi ea ana Shanguɗengha, Cha dledleɓti ea sǝnda ko i.', '');
INSERT or IGNORE INTO song VALUES (3, 'Dǝhǝ Sǝ tǝ Dunǝya Adanmǝ Ngǝ tǝ Mǝra', 'Sǝ dǝhǝu tǝ dunǝya, Ada mǝn ngǝ tǝ mǝra, Kǝl nda mǝna falnyi, Hyellǝu nacha mǝra. Nacha kwa shili na paru. Ma bǝjir tleka tǝ ɗa Dǝhǝ zhar nǝ fa kǝmǝn i. A mǝn kwa tari wa', 'Sǝ dǝhǝu tǝ dunǝya.........., Cha kwa mbǝlandi pǝchi. Mǝna kwa mǝr tlǝrni. Kutlǝr vu i ga anyi nga, mǝna kwa ɓǝlsar ni.', 'Sǝ dǝhǝu tǝ dunǝya.......... Dǝh sǝr pi i tǝ dunǝya, cha kwa nǝnda sǝrsum. Kalfi i, ka kǝ i eyakǝu zǝndǝti cha sanda ndo i.', 'Sǝ dǝhǝu tǝ dunǝya.......... Namǝn tsǝu njir nuǝnyi, dǝhǝ tlǝr chacha ngǝmǝn Ada nda kwa i mǝn, nacha ngǝ tǝ mǝr mǝn.','','');
INSERT or IGNORE INTO song VALUES (4, 'Dǝhǝ Sǝ Pi Pi Nda Fal Hyel', 'Sǝr dunǝya dǝhǝ ǝnyana, shilma mǝn fal Hyellǝu. Hyellǝu ndǝr gyalkur ta nǝcha, ndǝr gyalkur ba''anawa.', 'Nji inda Yesu tǝ chinda charma nyida kǝhi. Immanuel tsa a cha namen, ndǝr gyalkur pyamǝn cha.','Shanguɗǝ nǝ gyalkur mbabiya ea, ka ea pa Sartakǝu. Shanguɗǝ nǝ jir-jir ta nǝ cha, nacha ngǝ gavǝrma.', 'Nahi nji mǝnakǝ mbaɗǝ kǝhi, hyo gyatǝ patlanyi. Tsa a kǝhi mǝna kwa fal, mǝn kwa hǝtǝnyi sǝli', '','');